mapper.select:
SELECT ID 
FROM (
	SELECT RCV_ID AS ID FROM TB_CHAT WHERE CHAT_SPCE_ID = #{chatSpceId}
	UNION
	SELECT SEND_ID AS ID FROM TB_CHAT WHERE CHAT_SPCE_ID = #{chatSpceId}
	) AS sub
WHERE id != #{mbrId}

mapper.insert:
INSERT INTO TB_CHAT (
	CHAT_ID
	, CHAT_SPCE_ID
	, BBS_ID
	, SEND_ID
	, RCV_ID
	, CHAT_CN
	, SEND_DT
	, ATCH_FILE_NO
	, IDNTY_YN
	)
VALUES (
	#{chatId}
	, #{chatSpceId}
	, #{bbsId}
	, #{sendId}
	, #{rcvId}
	, #{chatCn}
	, now()
	, 0
	, 1
	)
