mapper.update:
 	UPDATE TB_BBS 
	SET FDBK = #{fdbk}, FDBK_DT = now()
 	WHERE BBS_ID = #{bbsId}

 	UPDATE TB_BBS 
	SET SLE_CMPTN_YN = 1, PRCH_ID = #{prchId}
 	WHERE BBS_ID = #{bbsId}

